<?php class Controller_User extends Controller_Website {	public function action_index()	{		//$this->request->redirect("home");	}	public function action_view()	{		$username = $this->request->param("username");		$user_model = Model::factory("user");		$blog_model = Model::factory("blog");		$frag_model = Model::factory("fragment");		$user = $user_model->get_user_info_by_username($username);		$recent_blogs = $blog_model->get_user_recent($user->get("id"));		$recent_frags = $frag_model->get_user_recent($user->get("id"));		$view = View::factory("user_profile")			->set("user",$user)			->set("blogs",$recent_blogs)			->set("frags",$recent_frags);		$this->response->body($view);	}}?>