<?php include Kohana::find_file('views', 'header'); ?>		<div class="blog_wrap">			<span class="blog_info"> written by <?php echo $user["username"];?> on <?php echo $blog["time_posted"]; ?></span>			<h3><?php echo $blog["title"];?></h3>			<div class="like_wrap">					<span class="number"><?php echo $blog['amount_votes'];?></span>					<span class="other">likes</span>			</div>			<div class="blog_content">			<p><?php echo BBCode::parse($blog["content"]);?></p><?php if($liked): ?>			<?php echo HTML::anchor("blog/unlike/".$blog["id"],"Unlike this blog",null,null,false);?><?php else: ?>			<?php echo HTML::anchor("blog/like/".$blog["id"],"Like this blog",null,null,false);?><?php endif; ?>			</div>		</div>		<h4>Comments</h4><?php if(count($comments) == 0): ?>		<p>There are no comments to display.</p><?php else: ?><?php foreach($comments as $comment): $user_data = $users_data[$comment['author_id']];?>		<div class="comment_wrap">			<div class="comment_topbar">				<img src="<?php echo $user_data->get('avatar');?>" height="100px" width="170px" />				<span><?php echo $user_data->get('username');?></span>			</div>			<div class="comment_content">				<p><?php echo BBCode::parse($comment['content']); ?></p>			</div>					</div><?php endforeach; ?><?php endif; ?>		<h4>Leave a comment</h4>		<form action="<?php echo URL::base();?>blog/comment/<?php echo $blog['id']; ?>" method="POST">			<label for="content">comment</label>			<textarea name="content" rows="10" cols="50"></textarea><br/>			<label>&nbsp;</label><input type="submit" value="Submit comment"/>		</form><?php include Kohana::find_file('views', 'footer'); ?>