<?php include Kohana::find_file('views', 'header'); ?>		<div class="sidebar">			<h3><?php echo $user['username'] ?>'s stats</h3>			<ul class="list_stats">				<li>Blogs <span><?php echo $user['count_blogs'] ?></span></li>				<li>Fragments <span><?php echo $user['count_fragments'] ?></span></li>				<li>Pageviews <span><?php echo $user['count_pageviews'] ?></span></li>				<li>Friends <span><?php echo $user['count_friends'] ?></span></li>				<li>Joined <span><?php echo DateHelper::human_readable($user['date_joined'],'') ?></span></li>				<li>Last seen <span><?php echo DateHelper::human_readable($user['date_last_seen'],'') ?></span></li>			</ul><?php $name = $user['name'];$age = ($user['age']<1)? '':$user['age'];$country = $user['country'];$gender = $user['gender'];if ($name OR $age OR $country OR $gender)://only show user's info if at least one of the fields is filled  $info = array('Name'=>$name, 'Age'=>$age, 'Country' => $country, 'Gender' => $gender);$keys_info = array_keys($info);?>			<h3><?php echo $user['username'] ?>'s info</h3>			<ul class="list_info"><?php foreach($keys_info as $item): 				if(!$info[$item]) continue;?>				<li><?php echo $item;?><span><?php echo $info[$item]; ?></li><?php endforeach; ?>			</ul><?php endif;?>			<div class="recent_frags">				<h3>recent frags</h3>				<ul class="frags"><?php foreach ($frags as $frag): ?>					<li><?php echo Link::fragment($frag['id'],$frag['content'],"frag_link"); ?> </li><?php endforeach; ?>				</ul>			</div>			</div>		<div class="main_wrap">			<h3><?php echo $user['username'] ?>'s profile</h3><?php foreach ($blogs as $blog): ?>			<div class="blog">				<h4 class="blog_title"><?php echo Link::blog($blog['id'],$blog['title'],$blog['title']); ?></h4>				<div class="like_wrap">					<span class="number"><?php echo $blog['amount_votes'];?></span>					<span class="other">likes</span>				</div>				<div class="blog_preview"><?php echo BBCode::parse(substr($blog['content'],0,400)); ?>				</div>			</div><?php endforeach; ?>		</div><?php include Kohana::find_file('views', 'footer'); ?>