<?php include Kohana::find_file('views', 'header'); ?>		<script language="javascript">		$(document).ready(function(){			$('#tab_blogs').click(function(){				$('.profile_recent_blogs').toggle(true);				$('.profile_recent_frags').toggle(false);				$(this).attr('class','active');				$('#tab_frags').attr('class','');			});			$('#tab_frags').click(function(){				$('.profile_recent_blogs').toggle(false);				$('.profile_recent_frags').toggle(true);				$(this).attr('class','active');				$('#tab_blogs').attr('class','');			});		});		</script>		<div class="sidebar">			<h3><?php echo $user['username'] ?>'s stats</h3>			<ul class="list_stats">				<li>Blogs <span><?php echo $user['count_blogs'] ?></span></li>				<li>Fragments <span><?php echo $user['count_fragments'] ?></span></li>				<li>Pageviews <span><?php echo $user['count_pageviews'] ?></span></li>				<li>Friends <span><?php echo $user['count_friends'] ?></span></li>				<li>Joined <span><?php echo DateHelper::human_readable($user['date_joined'],'') ?></span></li>				<li>Last seen <span><?php echo DateHelper::human_readable_day($user['date_last_seen'],'') ?></span></li>			</ul><?php $name = $user['name'];$age = ($user['age']<1)? '':$user['age'];$country = $user['country'];$gender = $user['gender'];if ($name OR $age OR $country OR $gender)://only show user's info if at least one of the fields is filled  $info = array('Name'=>$name, 'Age'=>$age, 'Country' => $country, 'Gender' => $gender);$keys_info = array_keys($info);?>			<h3><?php echo $user['username'] ?>'s info</h3>			<ul class="list_info"><?php foreach($keys_info as $item): 				if(!$info[$item]) continue;?>				<li><?php echo $item;?><span><?php echo $info[$item]; ?></li><?php endforeach; ?>			</ul><?php endif;?>		</div>		<div class="main_wrap">			<div class="tabs_menu">				<a href="#blogs" class="active" id="tab_blogs">Blogs</a>				<a href="#fragments" id="tab_frags">Fragments</a>			</div>			<h3><?php echo $user['username'] ?>'s profile</h3>			<div class="profile_recent_frags"><?php foreach ($frags as $frag): ?>				<div class="frag">					<?php echo Link::fragment($frag['id'],$frag['content'],'content');?>					<span class="info"><?php echo DateHelper::human_readable($frag['when'],''); ?></span>				</div><?php endforeach; ?>			</div>			<div class="profile_recent_blogs"><?php foreach ($blogs as $blog): ?>				<div class="blog">					<h4 class="blog_title"><?php echo Link::blog($blog['id'],$blog['title'],$blog['title']); ?></h4>					<div class="like_wrap">						<span class="number"><?php echo $blog['amount_votes'];?></span>						<span class="other">likes</span>					</div>					<div class="blog_preview"><?php echo BBCode::parse(substr($blog['content'],0,400)); ?>					</div>				</div><?php endforeach; ?>			</div>		</div><?php include Kohana::find_file('views', 'footer'); ?>