<?php include Kohana::find_file('views', 'header'); ?>		<div class="sidebar">			<h3><?php echo $user->get('username'); ?>'s stats</h3>			<ul class="list_stats">				<li><?php echo $user->get('count_blogs'); ?> blogs</li>				<li><?php echo $user->get('count_fragments'); ?> fragments</li>				<li><?php echo $user->get('count_pageviews'); ?> pageviews</li>				<li><?php echo $user->get('count_friends'); ?> friends</li>				<li>joined <?php echo $user->get('date_joined'); ?></li>				<li>last seen <?php echo $user->get('date_last_seen'); ?></li>			</ul>			<h3>recent frags</h3>			<ul class="frags"><?php foreach ($frags as $frag): ?>				<li><?php echo $frag['content']; ?></li><?php endforeach; ?>			</ul>		</div>		<div class="main_wrap">			<h3><?php echo $user->get('username'); ?>'s profile</h3><?php foreach ($blogs as $blog): $link = "blog/".$blog['id']."/".str_replace(array(" ","/"),array("-",""),$blog['title'])?>			<div class="blog">				<h4 class="blog_title"><?php echo HTML::anchor($link,$blog['title'],null,null,false); ?></h4>				<div class="like_wrap">					<span class="number"><?php echo $blog['amount_votes'];?></span>					<span class="other">likes</span>				</div>				<div class="blog_preview"><?php echo substr($blog['content'],0,400); ?>				</div>			</div><?php endforeach; ?>		</div><?php include Kohana::find_file('views', 'footer'); ?>